import React, { Component } from "react";

export default class PopUp extends Component {
    state = {
        seen: false,
        hover: false,
        text: "Click to learn about Gliese",
        b: "info about gliese a",
        c: "info about gliese c",
        ad: "info about gliese a and d"
    };


    togglePop = () => {
        console.log("I'm here")
        this.setState({
            seen: !this.state.seen,
            text: "now I'm gliese"
        });
    };

    toggleHoverPop = () => {
        console.log("I'm here")
        this.setState({
            hover: !this.state.hover
        });
    };

    handleClick = () => {
        this.props.toggle();
    };



    render() {
        return (
            <div className="modal">
                <div className="modal_content">
          <span className="close" onClick={this.handleClick}>
            &times;
          </span>
                        <h3>Register!</h3>
                    <div className="scaling-svg-container" style={{paddingBottom: "40%"}}>
                        <svg className="scaling-svg" viewBox="0 0 8830 2884"> {/* Needs auto
                         updating*/}
                            <image width="100%" height="100%" href="https://raw.githubusercontent.com/jgraves123/ESCovid-2.0/master/images/gliese_sys.png?raw=true"/>
                            <rect x="900" y="20" width="150" height="150" className="clickable" onClick={this.setState({text: this.state.b})}/>
                            <rect x="900" y="320" width="150" height="150" className="clickable" onClick={this.setState({text: this.state.c})}/>
                            <rect x="900" y="520" width="150" height="150" className="clickable" onClick={this.setState({text: this.state.ad})}/>
                            <rect x="900" y="920" width="150" height="150" className="clickable" onClick={this.setState({text: this.state.ad})}/>


                        </svg>
                    </div>
                    <div>
                        <h1>{this.state.text}</h1>
                    </div>
                </div>
            </div>
        );
    }
}
