import React, { Component } from "react";

export default class PopUp extends Component {
    state = {
        seen: false,
        hover: false,
        some_text: "hello me hi me"
    };


    togglePop = () => {
        console.log("I'm here")
        this.setState({
            seen: !this.state.seen
        });
    };

    toggleHoverPop = () => {
        console.log("I'm here")
        this.setState({
            hover: !this.state.hover
        });
    };

    handleClick = () => {
        this.props.toggle();
    };



    render() {
        return (
            <div className="modal">
                <div className="modal_content">
          <span className="close" onClick={this.handleClick}>
            &times;
          </span>
                        <h3>Register!</h3>
                    <div className="scaling-svg-container" style={{paddingBottom: "40%"}}>
                        <svg className="scaling-svg" viewBox="0 0 8830 2884"> {/* Needs auto
                         updating*/}
                            <image width="100%" height="100%" href="https://raw.githubusercontent.com/jgraves123/ESCovid-2.0/master/images/gliese_sys.png?raw=true"/>
                            <rect x="900" y="20" width="150" height="150" className="clickable" onClick={this.togglePop}/>
                            {/*{this.state.seen ? <PopUp toggle={this.togglePop} /> : null}*/}
                            <rect x="50" y="20" width="150" height="150" className="clickable" onClick={this.openDecision}/>
                            <rect x="250" y="220" width="150" height="150" className="clickable" onMouseEnter={this.toggleHoverPop} onMouseLeave={this.toggleHoverPop}/>
                        </svg>
                    </div>
                    <div>
                        {this.state.seen ? this.state.some_text : null}
                    </div>
                </div>
            </div>
        );
    }
}
