import React, { Component } from "react";

export default class TrappistPopUp extends Component {
    state = {
        seen: false,
        hover: false,
        text: "Hover over planets to learn more about them",
        e: "TRAPPIST-1 e is the most likely planet in the system to be habitable according to" +
            " research released in 2018. The planet is about earth sized and with a similar" +
            " density, leading to speculation that it consists of a similar rocky surface to the" +
            " earth. Planetary Habitability Catalog labeled it as \"one of the most potentially" +
            " habitable exoplanets discovered so far.\" Researchers estimate that the planet" +
            " has a 35% chance of being habitable.",
        fg: "info about trappist f and g",
        cdh: "info about trappist c, d and h",
        b: "info about trappist b"
    };

//     TRAPPIST-1 is one another star with potentially habitable planets. Located 40 lightyears away, it has seven terrestrial planets (consisting primarily of silicate metals and rocks), the most of any known planetary system. Three of them are in the habitable zone with a fourth right on the edge.</p>
//
// <p>TRAPPIST-1 e is the most likely planet in the system to be habitable according to research released in 2018. The planet is about earth sized and with a similar density, leading to speculation that it consists of a similar rocky surface to the earth. Planetary Habitability Catalog labeled it as "one of the most potentially habitable exoplanets discovered so far." Researchers estimate that the planet has a 35% chance of being habitable.</p>
//
// <p>TRAPPIST-1 f and g are also in the habitable zone, and c, d, and h are close to the range, but their relatively lower densities make them less likely to have a solid rock surface. Researchers estimate each of these has between a 5% and 15% chance of being habitable.


    togglePopE = () => {
        console.log("I'm here")
        this.setState({
            text: this.state.e
        });
    };

    togglePopFG = () => {
        console.log("I'm here")
        this.setState({
            text: this.state.fg
        });
    };

    togglePopCDH = () => {
        console.log("I'm here")
        this.setState({
            text: this.state.cdh
        });
    };

    togglePopB = () => {
        console.log("I'm here")
        this.setState({
            text: this.state.b
        });
    };

    toggleHoverPop = () => {
        console.log("I'm here")
        this.setState({
            hover: !this.state.hover
        });
    };

    handleClick = () => {
        this.props.toggle();
    };



    render() {
        return (
            <div className="modal">
                <div className="modal_content">
          <span className="close" onClick={this.handleClick}>
            &times;
          </span>
                    <h2 align="center" className="greytext">TRAPPIST-1</h2>
                    <h3 align="center">TRAPPIST-1 is a star located  40 lightyears away with seven terrestrial planets (composed primarily of silicate rocks or metals), which is the most of any known planetary system. Three of these planets are in the habitable zone with a fourth right on the edge.</h3>
                    <div className="scaling-svg-container" style={{paddingBottom: "45%"}}>
                        <svg className="scaling-svg" viewBox="0 0 1190 554"> {/* Needs auto
                         updating*/}
                            <image width="100%" height="100%" href="https://raw.githubusercontent.com/jgraves123/ESCovid-2.0/master/images/trappist_sys.jpg?raw=true"/>
                            <circle cx="130" cy="335" r="75" className="clickable-done" onMouseEnter={this.togglePopB}/>
                            <circle cx="295" cy="335" r="70" className="clickable-done" onMouseEnter={this.togglePopCDH}/>
                            <circle cx="465" cy="335" r="53" className="clickable-done" onMouseEnter={this.togglePopCDH}/>
                            <circle cx="630" cy="335" r="62" className="clickable-done" onMouseEnter={this.togglePopE}/>
                            <circle cx="798" cy="334" r="70" className="clickable-done" onMouseEnter={this.togglePopFG}/>
                            <circle cx="964" cy="333" r="76" className="clickable-done" onMouseEnter={this.togglePopFG}/>
                            <circle cx="1132" cy="333" r="50" className="clickable-done" onMouseEnter={this.togglePopCDH}/>


                        </svg>
                    </div>
                    <div>
                        <h3 align="center">{this.state.text}</h3>
                    </div>
                </div>
            </div>
        );
    }
}
